mapper.select:
select count(*)
from helf_trainer_attendance
where
	user_id = #{userId}

		and trainer_attendance_state = #{state}

select *
from (
	select 
		trainer_attendance_no				as no,
		trainer_attendance_date				as "date",
		user_id								as "user.id",
		trainer_attendance_cause			as cause,
		trainer_attendance_state			as state,
		row_number() over (order by trainer_attendance_no desc) row_number
	from helf_trainer_attendance
	where
		user_id = #{userId}

			and trainer_attendance_state = #{state}

	order by trainer_attendance_no desc
) 
where row_number between #{begin} and #{end}

WITH LatestOrder AS ( SELECT o.user_id, m.membership_name, o.orders_payment_date, mm.my_membership_remainder_cnt, mm.my_membership_enddate, mm.my_membership_state, ROW_NUMBER() OVER (PARTITION BY o.user_id ORDER BY o.orders_payment_date DESC) AS latest_rn FROM HELF_ORDERS o LEFT JOIN helf_my_membership mm ON o.my_membership_no = mm.my_membership_no LEFT JOIN helf_membership m ON mm.membership_no = m.membership_no ),
FirstOrder AS ( SELECT o.user_id, o.orders_payment_date, ROW_NUMBER() OVER (PARTITION BY o.user_id ORDER BY o.orders_payment_date ASC) AS first_rn FROM HELF_ORDERS o )
, AllData AS ( SELECT u.user_id as userId, u.user_name as userName, u.user_gender as userGender, u.user_tel as userTel, u.user_status as userStatus, f.orders_payment_date AS firstOrderDate, l.orders_payment_date AS latestOrderDate, l.membership_name AS MembershipName, l.my_membership_remainder_cnt AS myMembershipRemainderCnt, l.my_membership_enddate AS myMembershipEndDate, l.my_membership_state AS myMembershipState, ROW_NUMBER() OVER (ORDER BY u.user_name) AS row_number FROM HELF_USERS u LEFT JOIN FirstOrder f ON u.user_id = f.user_id AND f.first_rn = 1 LEFT JOIN LatestOrder l ON u.user_id = l.user_id AND l.latest_rn = 1 WHERE u.user_type = 'ROLE_USER'
AND u.user_name = INITCAP(#{keyword})
AND u.user_tel = #{keyword}
AND u.user_status = 'Y'
AND u.user_status = 'N'
AND l.my_membership_state = 'Y'
AND l.my_membership_state = 'N'
AND l.my_membership_remainder_cnt = #{remainderCnt}
AND l.my_membership_enddate BETWEEN #{remainingDays1} AND #{remainingDays2}
) SELECT * FROM AllData WHERE row_number BETWEEN #{begin} AND #{end} ORDER BY row_number

select * from (select distinct ca.customer_attendance_no as "customerAttendance.no", ca.customer_attendance_date as "customerAttendance.date", mc.category_name as "customerAttendance.myMembership.membership.category.name", m.membership_name as "customerAttendance.myMembership.membership.name", l.lesson_name as "customerAttendance.lessonName", row_number() over (order by ca.customer_attendance_date desc) rownumber from helf_customer_attendance ca, helf_my_membership mm, helf_membership m, helf_membership_category mc, helf_lesson_apply la, helf_lesson l where ca.my_membership_no = mm.my_membership_no and mm.membership_no = m.membership_no and m.category_no = mc.category_no and la.my_membership_no(+) = mm.my_membership_no and la.lesson_no = l.lesson_no(+) and ca.user_id = #{userId}
and ca.customer_attendance_date between #{specificDate1} and #{specificDate2}
) where rownumber between #{begin} and #{end}

select count(*)
from helf_customer_attendance
where user_id = #{userId}

select * from (select u.user_id as "user.id", u.user_email as "user.email", u.user_name as "user.name", u.user_tel as "user.tel", u.user_gender as "user.gender", u.user_status as "user.status", u.user_type as "user.type", t.trainer_no as trainerNo, t.trainer_file as trainerFile, t.trainer_resignation_date as resignationDate, t.trainer_title as title, t.trainer_hired_date as hiredDate, ROW_NUMBER() OVER (ORDER BY t.trainer_hired_date) AS row_number from helf_users u, helf_trainers t where u.user_id = t.user_id and u.user_type='ROLE_TRAINER'
and u.user_name = #{keyword}
and u.user_tel = #{keyword}
and u.user_status = 'Y'
and u.user_status = 'N'
and u.user_status = 'R'
and t.trainer_title = '점장'
and t.trainer_title = '직원'
) where row_number BETWEEN #{begin} AND #{end} ORDER BY row_number
